const l=document.querySelectorAll(".cart-count");let p=JSON.parse(localStorage.getItem("cart"))||[];l.forEach(t=>{t.textContent=p.length});const s=document.querySelectorAll(".addToCardBtn");s.forEach(t=>{t.addEventListener("click",()=>{let e=JSON.parse(localStorage.getItem("cart"))||[];l.forEach(r=>{r.textContent=e.length+1})})});document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll(".addToCardBtn").forEach(e=>{e.addEventListener("click",()=>{const r=e.parentElement.parentElement.parentElement.parentElement;let u=r.querySelector(".product-photo img").getAttribute("src"),d=r.querySelector(".product-name").innerHTML,c=r.querySelector(".product-price").innerHTML.slice(1),o=r.querySelector(".product__description").innerHTML;o=S(o,50);let n=Number(r.querySelector(".product-quantity .value").innerText),i=c*n;const a=JSON.parse(localStorage.getItem("cart"))||[];a.push({title:d,price:c,photo:u,description:o,quantity:n,wholePrice:i}),localStorage.setItem("cart",JSON.stringify(a))})})});function S(t,e){return t.length>e?t.substring(0,e)+"...":t}
